$(document).ready( function(){$('#accordion').accordion({heightStyle:"content",collapsible:true,beforeActivate:function(event,ui){if(ui.newHeader[0]){var currHeader  = ui.newHeader;var currContent = currHeader.next('.ui-accordion-content');}else{var currHeader  = ui.oldHeader;var currContent = currHeader.next('.ui-accordion-content');}var isPanelSelected = currHeader.attr('aria-selected') == 'true';currHeader.toggleClass('ui-corner-all',isPanelSelected).toggleClass('accordion-header-active ui-state-active ui-corner-top',!isPanelSelected).attr('aria-selected',((!isPanelSelected).toString()));currHeader.children('.ui-icon').toggleClass('ui-icon-triangle-1-e',isPanelSelected).toggleClass('ui-icon-triangle-1-s',!isPanelSelected);currContent.toggleClass('accordion-content-active',!isPanelSelected)
if(isPanelSelected){currContent.slideUp();}else{currContent.slideDown();}return false;}});});