function searchInputRedirect(search){if(search === ""){$("#return-best-sellers").html('<div class="space-top alert alert-danger alert-dismissible fade show" role="alert"><button type="button" class="close" data-dismiss="alert" aria-label="Close"><span aria-hidden="true" style="font-size:1rem;">&times;</span></button><strong>Não foi possível realizar a Busca o campo esta vazio!</strong></div>').slideDown(2000);return false;}else{window.location= newURL+'/pesquisa/'+search+'/';if(document.getElementById("autocomplete").value != ""){$("#autocomplete").hide();$("#replace").html('<input type="text" id="autocomplete" name="autocomplete" placeholder="Pesquisar" class="inp_pesq" >').show();return false;}}}